import React, { useState } from "react";
import "./Login.css";
import Art from "../../assets/images/Art.png"

export default function Login() {
  const [activeForm, setActiveForm] = useState({name:"",
    email
  });

  const signUpClick = () => {
    setActiveForm("signUp");
  };
  const signInClick = () => {
    setActiveForm("signIn");
  };

  return (
    <div>
      <div className="wrapper">
        <div className="blue-div">
          <div>
            <img src={Art} alt="image" />
            <h2>Welcome aboard my friend</h2>
            <p>just a couple of clicks and we start</p>
          </div>
        </div>
        <div className="login-sigup-form">
          {activeForm === "signIn" && (
            <div className="sign-in-div">
              <h1>Login</h1>
              <input type="email" placeholder="Email" />
              <input type="password" placeholder="Password"></input>
            </div>
          )}
          {activeForm === "signUp" && (
            <div className="sign-up-div">
              <h1>Register</h1>
              <input type="text" placeholder="Name" />
              <input type="email" placeholder="Email" />
              <input type="password" placeholder="Password"></input>
              <input type="password" placeholder="Confirm Password"></input>
            </div>
          )}
          <button
            className="signup-button"
            onClick={signUpClick}
            disabled={activeForm === "signUp"}
            style={{
              backgroundColor: activeForm === "signUp" ? "#17A2B8" : "white",
              color: activeForm === "signUp" ? "white" : "#17A2B8",
              border:
                activeForm === "signIn"
                  ? "1px solid #17A2B8"
                  : "1px solid white",
            }}
          >
            Register
          </button>
          <div className="havent-dv">Have no account yet?</div>
          <button
            className="login-button"
            onClick={signInClick}
            disabled={activeForm === "signIn"}
            style={{
              backgroundColor: activeForm === "signIn" ? "#17A2B8" : "white",
              color: activeForm === "signIn" ? "white" : "#17A2B8",
              border:
                activeForm === "signUp"
                  ? "1px solid #17A2B8"
                  : "1px solid white",
            }}
          >
            Login
          </button>
        </div>
      </div>
    </div>
  );
}
