import React, { useState, useEffect } from "react";
import "./Login.css";
import { registerUser } from "../../api/auth";
import { loginUser } from "../../api/auth";
import { useNavigate } from "react-router-dom";
import Art from "../../assets/images/Art.png";

export default function Login() {
  const [activeForm, setActiveForm] = useState("signUp");
  const [confirmPassError, setConfirmPassError] = useState("");
  const [registerFormData, setRegisterFormData] = useState(
    {
      name:"",
      email:"",
      password:"",
      confirmPassword:""
    }
  )

  const [loginFormData, setLoginFormData] = useState(
    {
      email:"",
      password:""
    }
  )

  const handleChange = (event) => {
    setRegisterFormData({ ...registerFormData, [event.target.name] : event.target.value });
  };

  const handleFormChange = (event) => {
    setLoginFormData({ ...loginFormData, [event.target.name] : event.target.value});
  }

  useEffect(() =>{
    console.log(registerFormData);
    if (registerFormData.confirmPassword === registerFormData.password){
      setConfirmPassError("")
    } else {
      confirmPassError.innerText = "Error! Password does not match"
    }
  }, [registerFormData]);

  useEffect(() =>{
    console.log(loginFormData);
  }, [loginFormData])

  const handleSubmit = async() => {
    if(
      !registerFormData.name ||
      !registerFormData.email ||
      !registerFormData.password
    ) {
      alert("Field can't be empty");
      return;
    }
    if(registerFormData.confirmPassword !== registerFormData.password)
      {
        alert("Passwords doesn't match!");
        return;
      }

    await registerUser(registerFormData);
    setActiveForm("signIn")
  }; 

  const handleFormSubmit = async() => {
    if(
      !loginFormData.email ||
      !loginFormData.password
     ) {
      alert("Field can't be empty");
      return;
    }
    const result = await loginUser(loginFormData);
    console.log(result);
    if (result) {
      // navigate("/");
    }
  }; 

  return (
    <div>
    <div className="wrapper">
      <div className="blue-div">
        <div>
          <img src={Art} alt="" />
          <h2>Welcome aboard my friend</h2>
          <p>just a couple of clicks and we start</p>
        </div>
      </div>
      <div className="login-sigup-form">
        {activeForm === "signIn" && (
          <div className="sign-in-div">
            <h1>Login</h1>
            <input type="email" name="email" onChange={handleFormChange} placeholder="Email" />
            <input type="password" name="password" onChange={handleFormChange} placeholder="Password"></input>
          </div>
        )}
        {activeForm === "signUp" && (
          <div className="sign-up-div">
            <h1>Register</h1>
            <input type="text" name="name" onChange={handleChange} placeholder="Name" />
            <input type="email" name="email" onChange={handleChange} placeholder="Email" />
            <input type="password" name="password" onChange={handleChange} placeholder="Password"></input>
            <input type="password" name="confirmPassword" onChange={handleChange} placeholder="Confirm Password"></input>
            <div id="confirmPassError"></div>
          </div>
        )}
        {activeForm === "signIn" ? (
          <>
            <button
              className="login-button"
              onClick={() => handleFormSubmit('login')}
              // disabled={true}
              style={{
                backgroundColor: "#17A2B8",
                color: "white",
                border: "1px solid #17A2B8",
              }}
            >
              Login
            </button>
            <div className="havent-dv">Have no account yet?</div>
            <button
              className="signup-button"
              onClick={() => setActiveForm('signUp')}
              style={{
                backgroundColor: "white",
                color: "#17A2B8",
                border: "1px solid #17A2B8",
              }}
            >
              Register
            </button>
          </>
        ) : (
          <>
            <button
              className="signup-button"
              onClick={() => handleSubmit('register')}
              // disabled={true}
              style={{
                backgroundColor: "#17A2B8",
                color: "white",
                border: "1px solid #17A2B8",
              }}
            >
              Register
            </button>
            <div className="havent-dv">Have an account?</div>
            <button
              className="login-button"
              onClick={() => setActiveForm("signIn")}
              style={{
                backgroundColor: "white",
                color: "#17A2B8",
                border: "1px solid #17A2B8",
              }}
            >
              Login
            </button>
          </>
        )}
      </div>
    </div>
  </div>
  );
}


